


/** 
 * @param {import('../types').userSettings} userSettings
 */
export const needleTransformCode = (command, config, userSettings) => {

    return [
        {
            name: 'needle-transform-code',
            transform(src, id) {
                if (id.includes("three__quarks.js")) {
                    const content = src.replace(
                        `console.log("%c Particle system powered by three.quarks. https://quarks.art/", "font-size: 16px; font-weight: bold;");`,
                        `setTimeout(() => console.log("Particle system powered by three.quarks. https://quarks.art/"), 100);`
                    );
                    return {
                        code: content,
                        map: null
                    }
                }
            },
        }
    ];
}